AmCharts.formatMilliseconds = function (string, date) {
    if (string.indexOf("fff") != -1) {
        var milliseconds = date.getMilliseconds();
        var mString = String(milliseconds);
        if (milliseconds < 10) {
            mString = "00" + milliseconds;
        }
        if (milliseconds >= 10 && milliseconds < 100) {
            mString = "0" + milliseconds;
        }

        string = string.replace(/fff/g, mString);
    }

    return string;
};

AmCharts.extractPeriod = function (period) {
    var cleanPeriod = AmCharts.stripNumbers(period);
    var count = 1;
    if (cleanPeriod != period) {
        count = Number(period.slice(0, period.indexOf(cleanPeriod)));
    }
    return {
        period: cleanPeriod,
        count: count
    };
};


// RESET DATE'S LOWER PERIODS TO MIN
AmCharts.resetDateToMin = function (date, period, count, firstDateOfWeek) {
    if (firstDateOfWeek === undefined) {
        firstDateOfWeek = 1;
    }

    var year;
    var month;
    var day;
    var hours;
    var minutes;
    var seconds;
    var milliseconds;
    var week_day;

    if (AmCharts.useUTC) {
        year = date.getUTCFullYear();
        month = date.getUTCMonth();
        day = date.getUTCDate();
        hours = date.getUTCHours();
        minutes = date.getUTCMinutes();
        seconds = date.getUTCSeconds();
        milliseconds = date.getUTCMilliseconds();
        week_day = date.getUTCDay();
    } else {
        year = date.getFullYear();
        month = date.getMonth();
        day = date.getDate();
        hours = date.getHours();
        minutes = date.getMinutes();
        seconds = date.getSeconds();
        milliseconds = date.getMilliseconds();
        week_day = date.getDay();
    }

    switch (period) {
        case "YYYY":
            year = Math.floor(year / count) * count;
            month = 0;
            day = 1;
            hours = 0;
            minutes = 0;
            seconds = 0;
            milliseconds = 0;
            break;

        case "MM":
            month = Math.floor(month / count) * count;
            day = 1;
            hours = 0;
            minutes = 0;
            seconds = 0;
            milliseconds = 0;
            break;

        case "WW":
            if (week_day === 0 && firstDateOfWeek > 0) {
                week_day = 7;
            }
            day = day - week_day + firstDateOfWeek;
            hours = 0;
            minutes = 0;
            seconds = 0;
            milliseconds = 0;
            break;

        case "DD":
            //day = Math.floor(day / count) * count;
            day = day;
            hours = 0;
            minutes = 0;
            seconds = 0;
            milliseconds = 0;
            break;

        case "hh":
            hours = Math.floor(hours / count) * count;
            minutes = 0;
            seconds = 0;
            milliseconds = 0;
            break;

        case "mm":
            minutes = Math.floor(minutes / count) * count;
            seconds = 0;
            milliseconds = 0;
            break;

        case "ss":
            seconds = Math.floor(seconds / count) * count;
            milliseconds = 0;
            break;

        case "fff":
            milliseconds = Math.floor(milliseconds / count) * count;
            break;
    }

    if (AmCharts.useUTC) {
        date = new Date();
        date.setUTCFullYear(year, month, day);
        //date.setUTCMonth(month);
        //date.setUTCDate(day);
        date.setUTCHours(hours, minutes, seconds, milliseconds);
        //date.setUTCMinutes(minutes);
        //date.setUTCSeconds(seconds);
        //date.setUTCMilliseconds(milliseconds);
    } else {
        date = new Date(year, month, day, hours, minutes, seconds, milliseconds);
    }


    return date;
};

AmCharts.getPeriodDuration = function (period, count) {
    if (count === undefined) {
        count = 1;
    }
    var duration;
    switch (period) {
        case "YYYY":
            duration = 31622400000;
            break;
        case "MM":
            duration = 2678400000;
            break;
        case "WW":
            duration = 604800000;
            break;
        case "DD":
            duration = 86400000;
            break;
        case "hh":
            duration = 3600000;
            break;
        case "mm":
            duration = 60000;
            break;
        case "ss":
            duration = 1000;
            break;
        case "fff":
            duration = 1;
            break;
    }
    return duration * count;
};


