Ext.define('NV.mplane.gui.LoginForm',{
	extend:'Ext.window.Window',
	config:{
		failed:false,
		errorMessage:"Wrong username or password"
	},
	constructor:function(config){
		this.callParent([Ext.apply(config,{
			layout:{
				type:'vbox',
				align:'center'
			},
			width:500,
			height:210,
			closable:false,
			title: "Mplane login",
			buttons:[
			   {
				   text:"Ok",
				   width:150,
				   cls:'menuButton',
				   handler: function(){
					   Ext.Ajax.request({
						  url:"/gui/login",
						  params:{
							  username:this.nameInput.getValue(),
							  password:this.passwordInput.getValue()
						  },
						  success: function(response){
							  var json = NV.ext.ux.nv.GlobalErrorHandler.jsonResponseErrorHandler(response);
	  							if (!json) {
	  								return;
	  							}	
	  							window.location.href='index.html';
						  },
						  scope:this
					   });
				   },
				   scope:this
			   }
			]
		})]);
		
		var array = document.location.search.substr(document.location.search.search("/?")+1).split("&");
		for(var i in array){
			var command = array[i].split("=")[0];
			var value = array[i].split("=")[1];
			if(command=='failure'){
				if(value=='true'){
					this.failed=true;
				}
			}
		}
		
		this.nameInput = Ext.create('Ext.form.field.Text', {
			inputType : "text",
			name : "name",
			fieldLabel : "Username",
			validateBlank : true,
			allowBlank : false,
			labelWidth : 140,
			msgTarget : "side",
			padding:'10 0 0 0'
		});

		this.passwordInput = Ext.create('Ext.form.field.Text', {
			inputType : "password",
			name : "password",
			fieldLabel : "Password",
			validateBlank : true,
			allowBlank : false,
			labelWidth : 140,
			msgTarget : "side",
			listeners: {
                specialkey: function(field, e){
                    // e.HOME, e.END, e.PAGE_UP, e.PAGE_DOWN,
                    // e.TAB, e.ESC, arrow keys: e.LEFT, e.RIGHT, e.UP, e.DOWN
                    if (e.getKey() == e.ENTER) {
                    	Ext.Ajax.request({
  						  url:"/gui/login",
  						  params:{
  							  username:this.nameInput.getValue(),
  							  password:this.passwordInput.getValue()
  						  },
  						  success: function(response){
  							var json = NV.ext.ux.nv.GlobalErrorHandler.jsonResponseErrorHandler(response);
  							if (!json) {
  								return;
  							}	
  							window.location.href='index.html';
  						  },
  						  scope:this
  					   });                    
                    }
                },
                scope:this
            }
			
		});
		
		this.errorMessage = Ext.create('Ext.form.Label',{
		        xtype: 'label',
		        forId: 'errorLabel',
		        style:{
		        	color:"red",
		        	fontWeight:"bold"
		        },
		        text: this.errorMessage,
		        margin: '0 0 0 10'
		});
		this.add(Ext.create('Ext.Img',{
			src:'resource/mplane-gui/image/mplane_final_logo.png',
			width:400,
			height:70,
			border:false
		}));
		this.add(this.nameInput);
		this.add(this.passwordInput);
		if(this.failed){
			this.add(this.errorMessage);
		}
	}
});

