Ext.define('NV.mplane.gui.windows.RunWindow',{
	extend:'Ext.window.Window',
		
	parent:undefined,
	
	constructor:function(config){
		
		var measure = config.measure;
		var parent = config.parent;
		
		var items = [];
		/*
		if(size>1){
			items.push(Ext.create('Ext.form.FieldContainer',{
				layout:'hbox',
		        anchor: '100%',
				items:[
				       {xtype:"label", text:"Capability:", width:"100px"},
				       {xtype:"label", text:measure.get("label")},
				]
			}));
		}*/
		
		items.push(Ext.create('Ext.form.field.Text',{
			anchor: '100%',
	        name: "when",
	        value: measure.raw.when,
	        fieldLabel: "When",
	        
//	        tooltip: 'When run?',
//	        tooltipType: "title",
//	        disabled: !editable,
//	        readOnlyCls: 'NvReadonlyField',
		}));
		
		var descriptors = NV.mplane.gui.Constants.parameterMap;
		for(var i in measure.raw.parameters){
			var actParam = measure.raw.parameters[i];
			var editable = false;
			if(actParam.trim()=="*"){
				//ki kell tölteni,
				editable = true;				
			}else if(actParam.indexOf("..")>=0 ){
				//szám és - jel van benne -> ki kell tölteni
				editable = true;
			}else if(descriptors[i].prim=='address' && actParam.indexOf("/")>=0 ){
				//ip cím és ki kell tölteni
				editable = true;
			}else if(actParam.indexOf(",")>=0 ){
				//felsorolás és ki kell tölteni
				editable = true;
			}else{
				//fix értéke van
				editable = false;
			}
			

			if(actParam.indexOf(",")>0){
				//felsorolás típusú
				var store = Ext.create('Ext.data.Store', {
				    fields: ['id', 'label'],
				    data : []
				});
				
				values = actParam.split(",");
				for(var j = 0; j <values.length; j++){
					store.add(values[j]);
				}
				
				items.push(Ext.create('Ext.form.ComboBox',{
				    fieldLabel: this.label,
				    name: i,
				    fieldLabel: i,
				    store: store,
				    queryMode: 'local',
				    displayField: 'label',
				    valueField: 'id',
		    	    typeAhead: false,
		    	    allowBlank: false,
		    	    forceSelection: true,
		    	    tooltipType: "title",
		    	    readOnlyCls: 'NvReadonlyField',
		    	    editable:editable
				}));
			}
			if(descriptors[i].prim=='natural' || descriptors[i].prim=='real'){
				
				var minValue, maxValue; 
				if(actParam.indexOf("..")>=0){
					minValue = actParam.substring(0,actParam.lastIndexOf("."));
					maxValue = actParam.substring(actParam.lastIndexOf(".")+1,actParam.length);
				}else{
					minValue = descriptors[i].prim == 'natural' ? 0 : undefined;
					maxValue = undefined;
				}
				
				
				
				items.push(Ext.create('Ext.form.field.Number',{
			        anchor: '100%',
			        name: i,
			        allowDecimals: descriptors[i].prim == 'natural' ? false : true,
			        step: descriptors[i].prim == 'natural' ? 1 : 0.1,				        		
			        fieldLabel: i,
			        tooltip: descriptors[i].desc,
			        value: actParam,
			        disabled: !editable,
			        tooltipType: "title",
			        minValue: minValue,
			        readOnlyCls: 'NvReadonlyField',
			        maxValue: maxValue
			    }));
			}
			else if(descriptors[i].prim=='address'){
				items.push(Ext.create('Ext.form.field.Text',{
			        anchor: '100%',
			        name: i,
			        value: actParam,
			        fieldLabel: i,
			        tooltip: descriptors[i].desc,
			        tooltipType: "title",
			        disabled: !editable,
			        readOnlyCls: 'NvReadonlyField',
			        regexText:'\b(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\b'
			    }));
			}else if(descriptors[i].prim=='string' || descriptors[i].prim=='url' ){
				items.push(Ext.create('Ext.form.field.Text',{
			        anchor: '100%',
			        name: i,
			        value:actParam,
			        fieldLabel: i,
			        disabled: !editable,
			        tooltipType: "title",
			        tooltip: descriptors[i].desc,
			        readOnlyCls: 'NvReadonlyField'
			    }));
			}else if(descriptors[i].prim=='time'){
				items.push(Ext.create('Ext.form.field.Text',{
			        anchor: '100%',
			        name: i,
			        fieldLabel: i,
			        tooltip: descriptors[i].desc,
			        tooltipType: "title",
			        disabled: !editable,
			        value:actParam,
			        readOnlyCls: 'NvReadonlyField'
		//	        regex:"\b(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\b"
			    }));
			}
			else if(descriptors[i].prim=='boolean'){
				var checkbox = Ext.create('Ext.form.field.Checkbox',{
					boxLabel  : '',
		            name      : i,
		            inputValue: true,
		            value:actParam,
		            padding:0,
		            disabled: !editable,
				});
				
				items.push(Ext.create('Ext.form.FieldContainer',
					{
			            fieldLabel: i,
			            defaultType: 'checkboxfield',
			            tooltipType: "title",
			            tooltip: descriptors[i].desc,
			            items: [
			                checkbox
			            ]
			        }));
				
			}
			
		}
		
		this.runForm = Ext.create('Ext.form.Panel',{
			items:items,
			layout:"vbox",
			title:false,
			padding:10,
			border:false
		});
		
		var me = this;
		this.callParent([{
			title : "Run "+measure.get("label"),
			items:this.runForm,
			layout:'vbox',
			buttons:[
			        
			         {
			        	 text:"Cancel",
			        	 handler: function(){
			        		 me.hide();
			        	 }
			         },
			         {
			        	 text:"Run",
			        	 handler: function(){
			        		 me.hide();
			        		 this.me.sendRunParamsToServer(Ext.clone(this.measure.raw), this.runForm.getValues());
			        	 },
			        	 scope:{
			        		 measure: measure,
			        		 runForm:this.runForm,
			        		 me:parent
			        	 }
			         }
			]
		}]);
	}
});

